import { useState, useEffect } from "react";
import { Check, Star, GraduationCap, Briefcase, Users, Crown, Zap, Sparkles } from "lucide-react";
import PaymentModal from "./payment-modal";
import { useToast } from "@/hooks/use-toast";

interface Package {
  id: string;
  name: string;
  description: string;
  price: string;
  features: string[];
  category: string;
  isPopular: boolean;
  isActive: boolean;
}

export default function PackagesSection() {
  const [packages, setPackages] = useState<Package[]>([]);
  const [loading, setLoading] = useState(true);
  const [selectedPackage, setSelectedPackage] = useState<Package | null>(null);
  const [showPaymentModal, setShowPaymentModal] = useState(false);
  const [activeFilter, setActiveFilter] = useState<string>("all");
  const { toast } = useToast();

  useEffect(() => {
    fetchPackages();
  }, []);

  const fetchPackages = async () => {
    try {
      const response = await fetch("/api/packages");
      const data = await response.json();
      setPackages(data || []);
    } catch (error) {
      console.error("Failed to fetch packages:", error);
      toast({
        title: "Error",
        description: "Failed to load packages. Please refresh the page.",
        variant: "destructive",
      });
    } finally {
      setLoading(false);
    }
  };

  const handlePackageSelect = (pkg: Package) => {
    setSelectedPackage(pkg);
    setShowPaymentModal(true);
  };

  const formatPrice = (price: string) => {
    return new Intl.NumberFormat("en-IN", {
      style: "currency",
      currency: "INR",
    }).format(parseFloat(price));
  };

  const getCategoryDisplayName = (category: string) => {
    switch (category) {
      case "students":
        return "Students";
      case "graduates":
        return "Graduates";
      case "professionals":
        return "Professionals";
      default:
        return category.charAt(0).toUpperCase() + category.slice(1);
    }
  };

  const getCategoryColor = (category: string) => {
    switch (category) {
      case "students":
        return "bg-gradient-to-r from-blue-500 to-cyan-500 text-white";
      case "graduates":
        return "bg-gradient-to-r from-green-500 to-emerald-500 text-white";
      case "professionals":
        return "bg-gradient-to-r from-purple-500 to-pink-500 text-white";
      default:
        return "bg-gradient-to-r from-gray-500 to-slate-500 text-white";
    }
  };

  const getCategoryIcon = (category: string) => {
    switch (category) {
      case "students":
        return <GraduationCap className="h-4 w-4" />;
      case "graduates":
        return <Users className="h-4 w-4" />;
      case "professionals":
        return <Briefcase className="h-4 w-4" />;
      default:
        return <Star className="h-4 w-4" />;
    }
  };

  // Filter packages by category
  const filteredPackages = activeFilter === "all" 
    ? packages 
    : packages.filter(pkg => pkg.category === activeFilter);

  // Sort packages - popular first, then by price
  const sortedPackages = [...filteredPackages].sort((a, b) => {
    if (a.isPopular && !b.isPopular) return -1;
    if (!a.isPopular && b.isPopular) return 1;
    return parseFloat(a.price) - parseFloat(b.price);
  });

  const filters = [
    { id: "all", label: "All solutions", icon: Star },
    { id: "students", label: "Students", icon: GraduationCap },
    { id: "graduates", label: "Graduates", icon: Users },
    { id: "professionals", label: "Professionals", icon: Briefcase },
  ];

  if (loading) {
    return (
      <section id="packages" className="scroll-mt-20 py-12 bg-card">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600 mx-auto mb-4"></div>
            <p className="text-muted-foreground">Loading packages...</p>
          </div>
        </div>
      </section>
    );
  }

  return (
    <>
      <section
        id="packages"
        className="scroll-mt-20 py-12 bg-muted"
      >
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center mb-16">
            <h2 className="text-3xl md:text-5xl font-bold mb-4">
              <span className="bg-gradient-to-r from-blue-600 via-purple-600 to-pink-600 bg-clip-text text-transparent">
                Our Solutions
              </span>
            </h2>
            <p className="text-xl text-muted-foreground max-w-3xl mx-auto">
              Choose the solution that best fits your career development needs
            </p>
          </div>

          {/* Filter Buttons */}
          <div className="flex flex-wrap justify-center gap-3 mb-12">
            {filters.map((filter) => (
              <button
                key={filter.id}
                onClick={() => setActiveFilter(filter.id)}
                className={`flex items-center gap-2 px-4 py-2 rounded-lg font-semibold transition-all duration-300 transform ${
                  activeFilter === filter.id
                    ? "bg-gradient-to-r from-blue-600 to-purple-600 text-white shadow-lg shadow-purple-500/50 scale-105"
                    : "bg-white dark:bg-gray-800 text-gray-700 dark:text-gray-300 hover:shadow-md hover:scale-105"
                }`}
                data-testid={`filter-${filter.id}`}
              >
                <filter.icon className="h-4 w-4" />
                {filter.label}
              </button>
            ))}
          </div>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-8 max-w-7xl mx-auto">
            {sortedPackages.map((pkg) => (
              <div
                key={pkg.id}
                className={`group bg-card p-6 rounded-xl hover-lift ${
                  pkg.isPopular 
                    ? "ring-2 ring-purple-400 shadow-xl shadow-purple-500/30 animate-pulse-glow" 
                    : "hover:shadow-xl hover:shadow-blue-500/20"
                } cursor-pointer relative transition-all duration-300`}
                data-testid={`package-${pkg.name.toLowerCase().replace(/\s+/g, "-")}`}
              >
                {pkg.isPopular && (
                  <div className="absolute -top-3 left-1/2 transform -translate-x-1/2 z-10">
                    <span className="bg-gradient-to-r from-yellow-400 via-orange-400 to-pink-500 text-white px-3 py-1 rounded-full text-xs font-bold flex items-center gap-1 shadow-lg shadow-yellow-500/50 animate-pulse">
                      <Crown className="h-3 w-3 animate-float" />
                      Popular
                    </span>
                  </div>
                )}

                {/* Category Tag */}
                <div className="mb-4">
                  <span
                    className={`inline-flex items-center gap-1 px-3 py-1 rounded-full text-xs font-bold ${getCategoryColor(pkg.category)}`}
                  >
                    {getCategoryIcon(pkg.category)}
                    {getCategoryDisplayName(pkg.category)}
                  </span>
                </div>

                {/* Package Icon */}
                <div className="mb-4 flex justify-center">
                  <div className={`w-12 h-12 bg-gradient-to-r ${
                    pkg.isPopular
                      ? "from-purple-500 to-pink-500"
                      : "from-blue-500 to-cyan-500"
                  } rounded-full flex items-center justify-center group-hover:scale-110 transition-transform duration-300 shadow-lg ${
                    pkg.isPopular ? "shadow-purple-500/50" : "shadow-blue-500/50"
                  }`}>
                    <Zap className="h-6 w-6 text-white group-hover:animate-pulse" />
                  </div>
                </div>

                <div className="text-center">
                  <h4 className="text-xl font-semibold mb-2">{pkg.name}</h4>
                  <p className="text-muted-foreground text-sm mb-4">{pkg.description}</p>
                  <div className="mb-4">
                    <span
                      className={`text-2xl font-bold ${
                        pkg.isPopular
                          ? "bg-gradient-to-r from-purple-600 to-pink-600"
                          : "bg-gradient-to-r from-blue-600 to-cyan-600"
                      } bg-clip-text text-transparent`}
                    >
                      {formatPrice(pkg.price)}
                    </span>
                  </div>

                  <ul className="text-left space-y-2 mb-4">
                    {pkg.features.map((feature, featureIndex) => (
                      <li key={featureIndex} className="flex items-start">
                        <div className="bg-green-100 rounded-full p-0.5 mr-2 mt-0.5">
                          <Check className="h-3 w-3 text-green-600" />
                        </div>
                        <span className="text-muted-foreground text-sm">{feature}</span>
                      </li>
                    ))}
                  </ul>

                  <button
                    onClick={() => handlePackageSelect(pkg)}
                    className={`w-full text-white py-2 rounded-lg font-semibold text-sm transition-all duration-300 hover:shadow-lg ${
                      pkg.isPopular
                        ? "bg-gradient-to-r from-purple-600 to-pink-600"
                        : "bg-gradient-to-r from-blue-600 to-cyan-600"
                    }`}
                    data-testid={`button-choose-${pkg.name.toLowerCase().replace(/\s+/g, "-")}`}
                  >
                    Choose Solution
                  </button>
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>

      <PaymentModal
        isOpen={showPaymentModal}
        onClose={() => setShowPaymentModal(false)}
        package={selectedPackage}
      />
    </>
  );
}

