        message: "Workshop booking submitted successfully",
        id: booking.id 
      });
    } catch (error) {
      if (error instanceof z.ZodError) {
        res.status(400).json({ 
          success: false, 
          message: "Invalid form data", 
          errors: error.errors 
        });
      } else {
        res.status(500).json({ 
          success: false, 
          message: "Internal server error"
        });
      }
    }
  });

  app.get("/api/workshop-bookings", requireAdmin, async (req, res) => {
    try {
      const bookings = await storage.getWorkshopBookings();
      res.json(bookings);
    } catch (error) {
      res.status(500).json({ 
        success: false, 
        message: "Failed to fetch workshop bookings" 
      });
    }
  });

  app.patch("/api/workshop-bookings/:id/status", requireAdmin, async (req, res) => {
    try {
      const { id } = req.params;
      const { status, adminNotes } = req.body;

      const updatedBooking = await storage.updateWorkshopBookingStatus(id, status, adminNotes);
      res.json({ success: true, booking: updatedBooking });
    } catch (error) {
      res.status(500).json({ 
        success: false, 
        message: "Failed to update booking status" 
      });
    }
  });

  app.delete("/api/workshop-bookings/:id", requireAdmin, async (req, res) => {
    try {
      const { id } = req.params;
      await storage.deleteWorkshopBooking(id);
      res.json({ success: true, message: "Workshop booking deleted successfully" });
    } catch (error) {
      res.status(500).json({ 
        success: false, 
        message: "Failed to delete workshop booking" 
      });
    }
  });

  // Serve public objects from storage
  app.get("/public-objects/:filePath(*)", async (req, res) => {
    const filePath = req.params.filePath;
    const { ObjectStorageService } = await import("./objectStorage");
    const objectStorageService = new ObjectStorageService();
    try {
      const file = await objectStorageService.searchPublicObject(filePath);
      if (!file) {
        return res.status(404).json({ error: "File not found" });
      }
      objectStorageService.downloadObject(file, res);
    } catch (error) {
      console.error("Error searching for public object:", error);
      return res.status(500).json({ error: "Internal server error" });
    }
  });

  // Admin endpoint to reset seed data
  app.post("/api/admin/reset-seed-data", requireAdmin, async (req, res) => {
    try {
      const { seedDatabase } = await import("./seed-data");

      // Get all existing blogs and testimonials
      const existingBlogs = await storage.getBlogArticles();
      const existingTestimonials = await storage.getTestimonials();

      // Delete all existing blogs
      for (const blog of existingBlogs) {
        await storage.deleteBlogArticle(blog.id);
      }

      // Delete all existing testimonials
      for (const testimonial of existingTestimonials) {
        await storage.deleteTestimonial(testimonial.id);
      }

      // Re-seed the database
      await seedDatabase();

      res.json({ 
        success: true, 
        message: "Seed data reset successfully",
        details: {
          blogsDeleted: existingBlogs.length,
          testimonialsDeleted: existingTestimonials.length
        }
      });
    } catch (error) {
      console.error("Error resetting seed data:", error);
      res.status(500).json({ 
        success: false, 
        message: "Failed to reset seed data",
        error: error instanceof Error ? error.message : "Unknown error"
      });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

