import * as React from "react"
import { ChevronLeft, ChevronRight } from "lucide-react"
import { DayPicker } from "react-day-picker"

import { cn } from "@/lib/utils"
import { buttonVariants } from "@/components/ui/button"

export type CalendarProps = React.ComponentProps<typeof DayPicker>

function Calendar({
  className,
  classNames,
  showOutsideDays = true,
  ...props
}: CalendarProps) {
  return (
    <DayPicker
      showOutsideDays={showOutsideDays}
      className={cn("p-3", className)}
      classNames={{
        months: "flex flex-col sm:flex-row space-y-4 sm:space-x-4 sm:space-y-0",
        month: "space-y-4",
        caption: "flex justify-center pt-1 relative items-center",
        caption_label: "text-sm font-medium",
        nav: "space-x-1 flex items-center",
        nav_button: cn(
          buttonVariants({ variant: "outline" }),
          "h-7 w-7 bg-transparent p-0 opacity-50 hover:opacity-100"
        ),
        nav_button_previous: "absolute left-1",
        nav_button_next: "absolute right-1",
        table: "w-full border-collapse space-y-1",
        head_row: "flex",
        head_cell:
          "text-muted-foreground rounded-md w-9 font-normal text-[0.8rem]",
        row: "flex w-full mt-2",
        cell: "h-9 w-9 text-center text-sm p-0 relative [&:has([aria-selected].day-range-end)]:rounded-r-md [&:has([aria-selected].day-outside)]:bg-accent/50 [&:has([aria-selected])]:bg-accent first:[&:has([aria-selected])]:rounded-l-md last:[&:has([aria-selected])]:rounded-r-md focus-within:relative focus-within:z-20",
        day: cn(
          buttonVariants({ variant: "ghost" }),
          "h-9 w-9 p-0 font-normal aria-selected:opacity-100"
        ),
        day_range_end: "day-range-end",
        day_selected:
          "bg-primary text-primary-foreground hover:bg-primary hover:text-primary-foreground focus:bg-primary focus:text-primary-foreground",
        day_today: "bg-accent text-accent-foreground",
        day_outside:
          "day-outside text-muted-foreground aria-selected:bg-accent/50 aria-selected:text-muted-foreground",
        day_disabled: "text-muted-foreground opacity-50",
        day_range_middle:
          "aria-selected:bg-accent aria-selected:text-accent-foreground",
        day_hidden: "invisible",
        ...classNames,
      }}
      components={{
        IconLeft: ({ className, ...props }) => (
          <ChevronLeft className={cn("h-4 w-4", className)} {...props} />
        ),
        IconRight: ({ className, ...props }) => (
          <ChevronRight className={cn("h-4 w-4", className)} {...props} />
        ),
      }}
      {...props}
    />
  )
}
Calendar.displayName = "Calendar"

export { Calendar }

client/src/index.css.content:
@import url('https://fonts.googleapis.com/css2?family=Inter:wght@300;400;500;600;700;800&display=swap');
@tailwind base;
@tailwind components;
@tailwind utilities;

:root {
  --background: hsl(210, 40%, 98%);
  --foreground: hsl(222.2, 100%, 10%);
  --card: hsl(0, 0%, 100%);
  --card-foreground: hsl(222.2, 100%, 10%);
  --popover: hsl(0, 0%, 100%);
  --popover-foreground: hsl(222.2, 100%, 10%);
  --primary: hsl(214, 90%, 55%);
  --primary-foreground: hsl(0, 0%, 100%);
  --secondary: hsl(210, 40%, 96%);
  --secondary-foreground: hsl(222.2, 100%, 10%);
  --muted: hsl(210, 40%, 96%);
  --muted-foreground: hsl(215.4, 30%, 28%);
  --accent: hsl(210, 40%, 96%);
  --accent-foreground: hsl(222.2, 100%, 10%);
  --destructive: hsl(0, 84.2%, 60.2%);
  --destructive-foreground: hsl(0, 0%, 100%);
  --border: hsl(214.3, 31.8%, 91.4%);
  --input: hsl(214.3, 31.8%, 91.4%);
  --ring: hsl(214, 90%, 55%);

  /* Professional Brand Colors */
  --careerskope-blue: hsl(220, 60%, 50%);
  --careerskope-blue-light: hsl(220, 60%, 60%);
  --careerskope-blue-dark: hsl(220, 60%, 40%);
  --careerskope-purple: hsl(260, 50%, 55%);
  --careerskope-accent: hsl(220, 60%, 95%);

  /* Simplified Gradients */
  --gradient-primary: linear-gradient(135deg, hsl(220, 60%, 50%) 0%, hsl(260, 50%, 55%) 100%);
  --gradient-hero: linear-gradient(135deg, hsl(220, 60%, 96%) 0%, hsl(260, 40%, 98%) 100%);
  --gradient-card: linear-gradient(145deg, rgba(255, 255, 255, 0.95) 0%, rgba(255, 255, 255, 0.9) 100%);

  /* Rainbow Background Animation */
  --rainbow-opacity: 0.15;

  --font-sans: 'Inter', sans-serif;
  --radius: 0.75rem;
}

.dark {
  --background: hsl(222.2, 84%, 4.9%);
  --foreground: hsl(210, 40%, 98%);
  --card: hsl(222.2, 84%, 4.9%);
  --card-foreground: hsl(210, 40%, 98%);
  --popover: hsl(222.2, 84%, 4.9%);
  --popover-foreground: hsl(210, 40%, 98%);
  --primary: hsl(210, 40%, 98%);
  --primary-foreground: hsl(222.2, 84%, 4.9%);
  --secondary: hsl(217.2, 32.6%, 17.5%);
  --secondary-foreground: hsl(210, 40%, 98%);
  --muted: hsl(217.2, 32.6%, 17.5%);
  --muted-foreground: hsl(215, 20.2%, 65.1%);
  --accent: hsl(217.2, 32.6%, 17.5%);
  --accent-foreground: hsl(210, 40%, 98%);
  --destructive: hsl(0, 62.8%, 30.6%);
  --destructive-foreground: hsl(210, 40%, 98%);
  --border: hsl(217.2, 32.6%, 17.5%);
  --input: hsl(217.2, 32.6%, 17.5%);
  --ring: hsl(212.7, 26.8%, 83.9%);

  /* Rainbow Background Animation - reduced for dark mode */
  --rainbow-opacity: 0.10;
}

@layer base {
  * {
    @apply border-border;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    text-rendering: optimizeLegibility;
  }
  body {
    @apply bg-background text-foreground font-sans antialiased;
    position: relative;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    font-feature-settings: "kern" 1, "liga" 1;
    image-rendering: -webkit-optimize-contrast;
    image-rendering: crisp-edges;
  }

  /* Animated Rainbow Background */
  body::before {
    content: '';
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    z-index: -1;
    pointer-events: none;
    background: conic-gradient(
      from 0deg,
      #ff0080,
      #ff8c00,
      #ffd700,
      #00ff00,
      #00ffff,
      #0080ff,
      #8000ff,
      #ff0080
    );
    opacity: var(--rainbow-opacity);
    animation: rainbow-rotate 30s linear infinite;
    will-change: transform;
    filter: blur(80px);
  }

  @media (prefers-reduced-motion: reduce) {
    body::before {
      animation: none;
    }
  }

  /* Utility to disable rainbow on specific sections */
  .no-rainbow {
    position: relative;
  }

  .no-rainbow::before {
    content: '';
    position: absolute;
    inset: 0;
    background: var(--background);
    z-index: -1;
  }

  /* Responsive Typography with clamp() */
  h1 {
    font-size: clamp(2rem, 5vw, 4rem);
    line-height: 1.1;
    font-weight: 800;
    letter-spacing: -0.02em;
    color: hsl(222.2, 100%, 8%);
  }

  h2 {
    font-size: clamp(1.5rem, 4vw, 3rem);
    line-height: 1.2;
    font-weight: 700;
    letter-spacing: -0.015em;
    color: hsl(222.2, 95%, 10%);
  }

  h3 {
    font-size: clamp(1.25rem, 3vw, 2rem);
    line-height: 1.3;
    font-weight: 600;
    letter-spacing: -0.01em;
    color: hsl(222.2, 90%, 12%);
  }

  p {
    font-size: clamp(1rem, 2vw, 1.125rem);
    line-height: 1.7;
    text-align: left;
    max-width: 65ch;
    color: hsl(215.4, 30%, 18%);
    font-weight: 400;
    font-optical-sizing: auto;
  }

  /* Respect reduced motion preferences */
  @media (prefers-reduced-motion: reduce) {
    *,
    *::before,
    *::after {
      animation-duration: 0.01ms !important;
      animation-iteration-count: 1 !important;
      transition-duration: 0.01ms !important;
      scroll-behavior: auto !important;
    }
  }
}

@layer components {
  /* Elegant section width constraint */
  .section-elegant {
    max-width: 1400px;
    margin-left: auto;
    margin-right: auto;
  }

  .gradient-bg {
      background: var(--gradient-hero);
      position: relative;
  }

  .gradient-bg::before {
    content: '';
    position: absolute;
    inset: 0;
    background: linear-gradient(
      45deg,
      rgba(255, 255, 255, 0.1) 0%,
      transparent 50%,
      rgba(255, 255, 255, 0.05) 100%
    );
    pointer-events: none;
  }

  .modern-card {
    background: rgba(255, 255, 255, 0.98);
    backdrop-filter: blur(12px);
    border: 1px solid rgba(0, 0, 0, 0.08);
    box-shadow: 
      0 4px 16px rgba(0, 0, 0, 0.1),
      0 1px 3px rgba(0, 0, 0, 0.06);
  }

  .enhanced-card {
    background: linear-gradient(145deg, rgba(255, 255, 255, 0.95) 0%, rgba(255, 255, 255, 0.8) 100%);
    backdrop-filter: blur(25px);
    border: 1px solid rgba(255, 255, 255, 0.3);
    box-shadow: 
      0 12px 40px rgba(0, 0, 0, 0.15),
      0 2px 16px rgba(0, 0, 0, 0.1),
      0 1px 0px rgba(255, 255, 255, 0.5) inset;
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .enhanced-card:hover {
    background: linear-gradient(145deg, rgba(255, 255, 255, 0.98) 0%, rgba(255, 255, 255, 0.85) 100%);
    box-shadow: 
      0 20px 50px rgba(0, 0, 0, 0.2),
      0 4px 20px rgba(0, 0, 0, 0.15),
      0 1px 0px rgba(255, 255, 255, 0.6) inset;
    transform: translateY(-2px) scale(1.02);
  }

  .vibrant-button {
    background: var(--careerskope-blue);
    color: white;
    transition: all 0.2s ease;
    font-weight: 600;
    letter-spacing: 0.01em;
    box-shadow: 0 2px 8px rgba(59, 130, 246, 0.3);
  }

  .vibrant-button:hover {
    background: var(--careerskope-blue-dark);
    transform: translateY(-2px);
    box-shadow: 
      0 6px 20px rgba(59, 130, 246, 0.4),
      0 2px 8px rgba(0, 0, 0, 0.15);
  }

  .service-icon {
    background: linear-gradient(135deg, var(--careerskope-blue), var(--careerskope-purple));
    padding: 1rem;
    border-radius: 1rem;
    display: inline-flex;
    align-items: center;
    justify-content: center;
    margin-bottom: 1rem;
    transition: all 0.3s ease;
  }

  .service-icon:hover {
    transform: translateY(-4px) scale(1.05);
    box-shadow: 0 10px 30px rgba(0, 0, 0, 0.2);
  }


  .glass-effect {
    background: rgba(255, 255, 255, 0.1);
    backdrop-filter: blur(10px);
    border: 1px solid rgba(255, 255, 255, 0.2);
  }


  .nav-link {
    position: relative;
    transition: color 0.3s ease;
  }

  .nav-link::after {
    content: '';
    position: absolute;
    width: 0;
    height: 2px;
    bottom: -5px;
    left: 0;
    background: linear-gradient(90deg, hsl(221.2, 83.2%, 53.3%), hsl(0, 84.2%, 60.2%));
    transition: width 0.3s ease;
  }

  .nav-link:hover::after {
    width: 100%;
  }

  .pricing-card {
    position: relative;
    overflow: hidden;
  }

  .pricing-card::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.4), transparent);
    transition: left 0.6s ease;
  }

  .pricing-card:hover::before {
    left: 100%;
  }

  .testimonial-card {
    min-height: 200px;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
  }
}

@layer utilities {
  .scroll-smooth {
    scroll-behavior: smooth;
  }

  /* Image sharpness */
  img {
    image-rendering: -webkit-optimize-contrast;
    image-rendering: crisp-edges;
    backface-visibility: hidden;
    transform: translateZ(0);
  }

  /* Animation utilities */
  .animate-fade-in {
    animation: fadeIn 0.6s ease-in-out;
  }

  .animate-slide-up {
    animation: slideUp 0.8s ease-out;
  }

  .animate-slide-in-left {
    animation: slideInLeft 0.8s ease-out;
  }

  .animate-slide-in-right {
    animation: slideInRight 0.8s ease-out;
  }

  .animate-scale-in {
    animation: scaleIn 0.6s ease-out;
  }

  .animate-float {
    animation: float 3s ease-in-out infinite;
  }

  .animate-pulse-glow {
    animation: pulseGlow 2s ease-in-out infinite;
  }

  .animate-ken-burns {
    animation: kenBurns 20s ease-in-out infinite alternate;
  }

  .animate-shimmer {
    background: linear-gradient(
      90deg,
      transparent 0%,
      rgba(255, 255, 255, 0.4) 50%,
      transparent 100%
    );
    background-size: 200% 100%;
    animation: shimmer 3s infinite;
  }

  .gradient-text {
    background: linear-gradient(
      90deg,
      #3b82f6,
      #ec4899,
      #f59e0b,
      #10b981,
      #3b82f6
    );
    background-size: 200% auto;
    -webkit-background-clip: text;
    background-clip: text;
    -webkit-text-fill-color: transparent;
    animation: gradientText 3s linear infinite;
  }

  .glow-effect {
    animation: glowPulse 2s ease-in-out infinite;
  }

  .border-glow {
    animation: borderGlow 3s ease-in-out infinite;
  }

  /* Floating Particles Background */
  .particles-bg {
    position: absolute;
    inset: 0;
    overflow: hidden;
    pointer-events: none;
    z-index: 0;
  }

  .particle {
    position: absolute;
    width: 4px;
    height: 4px;
    background: radial-gradient(circle, rgba(255, 255, 255, 0.8) 0%, transparent 70%);
    border-radius: 50%;
    animation: floatParticle 15s infinite ease-in-out;
  }

  /* Ripple effect for buttons */
  @keyframes ripple {
    to {
      transform: scale(4);
      opacity: 0;
    }
  }

  /* Reduced motion support for all animations */
  @media (prefers-reduced-motion: reduce) {
    .animate-slide-up,
    .animate-fade-in,
    .animate-float,
    .animate-pulse-glow,
    .animate-bounce-in {
      animation: none !important;
    }

    * {
      transition-duration: 0.01ms !important;
      animation-duration: 0.01ms !important;
    }
  }

  .animate-bounce-in {
    animation: bounceIn 0.8s ease-out;
  }

  /* Stagger delays for multiple elements */
  .animate-delay-100 {
    animation-delay: 0.1s;
  }

  .animate-delay-200 {
    animation-delay: 0.2s;
  }

  .animate-delay-300 {
    animation-delay: 0.3s;
  }

  .animate-delay-400 {
    animation-delay: 0.4s;
  }

  .animate-delay-500 {
    animation-delay: 0.5s;
  }

  /* Hover and interaction effects */
  .hover-lift {
    transition: transform 0.3s ease, box-shadow 0.3s ease;
  }

  .hover-lift:hover {
    transform: translateY(-8px);
    box-shadow: 0 20px 40px rgba(0, 0, 0, 0.1);
  }

  .hover-tilt {
    transition: transform 0.3s ease;
  }

  .hover-tilt:hover {
    transform: rotate(2deg);
  }

  .hover-scale {
    transition: transform 0.3s ease;
  }

  /* Navigation responsive utilities */
  .nav-responsive {
    @media (max-width: 640px) {
      font-size: 0.875rem;
      padding: 0.5rem 0.75rem;
    }
    @media (min-width: 641px) and (max-width: 1024px) {
      font-size: 0.875rem;
      padding: 0.5rem 1rem;
    }
    @media (min-width: 1025px) {
      font-size: 1rem;
      padding: 0.75rem 1.25rem;
    }
  }

  /* Container responsive max-widths */
  .container-responsive {
    @media (max-width: 640px) {
      max-width: 100%;
      padding-left: 1rem;
      padding-right: 1rem;
    }
    @media (min-width: 641px) and (max-width: 768px) {
      max-width: 100%;
      padding-left: 1.5rem;
      padding-right: 1.5rem;
    }
    @media (min-width: 769px) and (max-width: 1024px) {
      max-width: 100%;
      padding-left: 2rem;
      padding-right: 2rem;
    }
  }

  .hover-scale:hover {
    transform: scale(1.05);
  }

  /* Enhanced Interactive button effects */
  .btn-interactive {
    position: relative;
    overflow: hidden;
    transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
    transform-style: preserve-3d;
  }

  .btn-interactive::before {
    content: '';
    position: absolute;
    top: 50%;
    left: 50%;
    width: 0;
    height: 0;
    background: radial-gradient(circle, rgba(255, 255, 255, 0.3) 0%, transparent 70%);
    border-radius: 50%;
    transform: translate(-50%, -50%);
    transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .btn-interactive:hover::before {
    width: 300px;
    height: 300px;
  }

  .btn-interactive:hover {
    transform: translateY(-2px);
    box-shadow: 
      0 10px 40px rgba(0, 0, 0, 0.2),
      0 0 0 1px rgba(255, 255, 255, 0.1) inset;
  }


  /* Scroll progress bar */
  .scroll-progress {
    position: fixed;
    top: 0;
    left: 0;
    height: 4px;
    background: linear-gradient(90deg, var(--careerskope-blue), var(--careerskope-green), var(--careerskope-red));
    z-index: 1000;
    transition: width 0.1s ease;
  }

  /* Sparkle Animation */
  .sparkle-container {
    pointer-events: none;
  }

  .sparkle {
    position: absolute;
    font-size: 12px;
    opacity: 0;
    animation: sparkle-float 3s infinite ease-in-out;
  }

  @keyframes sparkle-float {
    0%, 100% {
      opacity: 0;
      transform: translateY(0px) scale(0);
    }
    50% {
      opacity: 1;
      transform: translateY(-20px) scale(1);
    }
  }

  /* Floating Icon Animation */
  .floating-icon {
    animation: float 3s ease-in-out infinite;
  }

  @keyframes float {
    0%, 100% {
      transform: translateY(0px);
    }
    50% {
      transform: translateY(-10px);
    }
  }

  /* Custom Animations */
  @keyframes spin-slow {
    from {
      transform: rotate(0deg);
    }
    to {
      transform: rotate(360deg);
    }
  }

  @keyframes spin-reverse {
    from {
      transform: rotate(360deg);
    }
    to {
      transform: rotate(0deg);
    }
  }

  .animate-spin-slow {
    animation: spin-slow 8s linear infinite;
  }

  .animate-spin-reverse {
    animation: spin-reverse 12s linear infinite;
  }
}

@keyframes fadeIn {
  from { opacity: 0; }
  to { opacity: 1; }
}

@keyframes slideUp {
  from { 
    opacity: 0;
    transform: translateY(30px);
  }
  to { 
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes slideInLeft {
  from { 
    opacity: 0;
    transform: translateX(-50px);
  }
  to { 
    opacity: 1;
    transform: translateX(0);
  }
}

@keyframes slideInRight {
  from { 
    opacity: 0;
    transform: translateX(50px);
  }
  to { 
    opacity: 1;
    transform: translateX(0);
  }
}

@keyframes scaleIn {
  from { 
    opacity: 0;
    transform: scale(0.8);
  }
  to { 
    opacity: 1;
    transform: scale(1);
  }
}

@keyframes bounceIn {
  0% {
    opacity: 0;
    transform: scale(0.3);
  }
  50% {
    opacity: 1;
    transform: scale(1.05);
  }
  70% {
    transform: scale(0.9);
  }
  100% {
    opacity: 1;
    transform: scale(1);
  }
}

@keyframes pulseGlow {
  0%, 100% {
    box-shadow: 0 0 20px rgba(59, 130, 246, 0.5);
  }
  50% {
    box-shadow: 0 0 40px rgba(59, 130, 246, 0.8);
  }
}

@keyframes float {
  0%, 100% { transform: translateY(0px); }
  50% { transform: translateY(-10px); }
}

@keyframes slide {
  0% {
    background-position: 0 0;
  }
  100% {
    background-position: 60px 60px;
  }
}

@keyframes rainbow-rotate {
  0% {
    transform: rotate(0deg) scale(1.5);
  }
  100% {
    transform: rotate(360deg) scale(1.5);
  }
}

/* Ken Burns Effect - Zoom and Pan for Hero Images */
@keyframes kenBurns {
  0% {
    transform: scale(1) translate(0, 0);
  }
  100% {
    transform: scale(1.15) translate(-5%, -5%);
  }
}

@keyframes kenBurnsReverse {
  0% {
    transform: scale(1.15) translate(-5%, -5%);
  }
  100% {
    transform: scale(1) translate(0, 0);
  }
}

/* Shimmer Effect */
@keyframes shimmer {
  0% {
    background-position: -1000px 0;
  }
  100% {
    background-position: 1000px 0;
  }
}

/* Gradient Text Animation */
@keyframes gradientText {
  0%, 100% {
    background-position: 0% 50%;
  }
  50% {
    background-position: 100% 50%;
  }
}

/* Glow Pulse */
@keyframes glowPulse {
  0%, 100% {
    box-shadow: 0 0 20px rgba(255, 215, 0, 0.5), 0 0 40px rgba(255, 105, 180, 0.3);
  }
  50% {
    box-shadow: 0 0 40px rgba(255, 215, 0, 0.8), 0 0 80px rgba(255, 105, 180, 0.6);
  }
}

/* Floating particles */
@keyframes floatParticle {
  0%, 100% {
    transform: translate(0, 0) rotate(0deg);
    opacity: 0.8;
  }
  25% {
    transform: translate(20px, -30px) rotate(90deg);
    opacity: 1;
  }
  50% {
    transform: translate(-20px, -60px) rotate(180deg);
    opacity: 0.8;
  }
  75% {
    transform: translate(30px, -40px) rotate(270deg);
    opacity: 1;
  }
}

/* Border Glow Animation */
@keyframes borderGlow {
  0%, 100% {
    border-color: rgba(59, 130, 246, 0.5);
    box-shadow: 0 0 20px rgba(59, 130, 246, 0.3);
  }
  50% {
    border-color: rgba(236, 72, 153, 0.8);
    box-shadow: 0 0 40px rgba(236, 72, 153, 0.6);
  }
}

