import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import type { BlogArticle } from "@shared/schema";
import BlogDetailModal from "./blog-detail-modal";

export default function BlogSection() {
  const [activeFilter, setActiveFilter] = useState("all");
  const [showAll, setShowAll] = useState(false);
  const [selectedArticle, setSelectedArticle] = useState<BlogArticle | null>(null);
  const [showModal, setShowModal] = useState(false);

  const filters = [
    { key: "all", label: "All Articles" },
    { key: "students", label: "Students" },
    { key: "graduates", label: "Graduates" },
    { key: "professionals", label: "Professionals" }
  ];

  // Fetch blog articles from database
  const { data: articles = [], isLoading } = useQuery<BlogArticle[]>({
    queryKey: ['/api/blog-articles', activeFilter],
    queryFn: () => {
      const params = activeFilter !== "all" ? `?category=${activeFilter}` : "";
      return fetch(`/api/blog-articles${params}`).then(res => res.json());
    }
  });

  // Add styling based on category
  const getArticleStyles = (category: string) => {
    switch (category) {
      case "students":
        return { color: "text-blue-600", hoverColor: "hover:text-blue-700" };
      case "graduates":
        return { color: "text-green-600", hoverColor: "hover:text-green-700" };
      case "professionals":
        return { color: "text-red-600", hoverColor: "hover:text-red-700" };
      default:
        return { color: "text-gray-600", hoverColor: "hover:text-gray-700" };
    }
  };

  const filteredArticles = Array.isArray(articles) ? articles : [];

  // Show only first 3 articles unless "showAll" is true
  const displayedArticles = showAll ? filteredArticles : filteredArticles.slice(0, 3);

  const handleReadMore = (article: BlogArticle) => {
    setSelectedArticle(article);
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
    setSelectedArticle(null);
  };

  const handleViewAllArticles = () => {
    setShowAll(true);
  };

  return (
    <section id="blog" className="scroll-mt-20 py-12 bg-card">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="text-center mb-16">
          <h2 className="text-3xl md:text-5xl font-bold mb-4">
            <span className="bg-gradient-to-r from-orange-500 via-red-500 to-pink-500 bg-clip-text text-transparent">
              Resources
            </span>
          </h2>
          <p className="text-xl text-muted-foreground max-w-3xl mx-auto">
          Stay updated with Industry trends, Career development insights, and inputs on personal growth
          </p>
        </div>


        {isLoading ? (
          <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
            {[1, 2, 3].map((i) => (
              <div key={i} className="bg-card p-6 rounded-xl animate-pulse text-center">
                <div className="mb-4 flex justify-center">
                  <div className="bg-gray-200 rounded-full h-12 w-12"></div>
                </div>
                <div className="bg-gray-200 rounded h-3 w-16 mb-2 mx-auto"></div>
                <div className="bg-gray-200 rounded h-5 w-full mb-3"></div>
                <div className="bg-gray-200 rounded h-4 w-full mb-2"></div>
                <div className="bg-gray-200 rounded h-4 w-3/4 mb-4 mx-auto"></div>
                <div className="flex items-center justify-between">
                  <div className="bg-gray-200 rounded h-3 w-12"></div>
                  <div className="bg-gray-200 rounded h-3 w-16"></div>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
            {displayedArticles.map((article) => {
              const styles = getArticleStyles(article.category);
              return (
                <article 
                  key={article.id}
                  className="bg-card rounded-xl hover-lift text-center border-2 border-transparent hover:border-purple-300 hover:shadow-xl hover:shadow-purple-500/20 transition-all duration-300 overflow-hidden"
                  data-testid={`blog-card-${article.category}`}
                >
                  {article.imageUrl ? (
                    <div className="w-full h-48 overflow-hidden">
                      <img 
                        src={article.imageUrl} 
                        alt={article.title}
                        className="w-full h-full object-cover transition-transform duration-300 hover:scale-110"
                        onError={(e) => {
                          const target = e.target as HTMLImageElement;
                          target.style.display = 'none';
                          target.nextElementSibling?.classList.remove('hidden');
                        }}
                      />
                      <div className="hidden mb-4 flex justify-center items-center h-48">
                        <div className="text-4xl">ðŸ“š</div>
                      </div>
                    </div>
                  ) : (
                    <div className="mb-4 flex justify-center items-center h-48">
                      <div className="text-4xl">ðŸ“š</div>
                    </div>
                  )}
                  <div className="p-6">
                    <div className={`text-xs font-semibold mb-2 capitalize ${styles.color}`}>
                      {article.category}
                    </div>
                    <h3 className="text-xl font-semibold mb-3">{article.title}</h3>
                    <p className="text-muted-foreground text-sm mb-4 line-clamp-3">{article.description}</p>
                    <div className="flex items-center justify-between">
                      <span className="text-xs text-muted-foreground">{article.readTime}</span>
                      <button 
                        onClick={() => handleReadMore(article)}
                        className={`font-semibold text-sm ${styles.color} ${styles.hoverColor} transition-colors duration-200`} 
                        data-testid={`button-read-more-${article.id}`}
                      >
                        Read More
                      </button>
                    </div>
                  </div>
                </article>
              );
            })}
          </div>
        )}

        {!showAll && filteredArticles.length > 3 && (
          <div className="text-center mt-12">
            <button 
              onClick={handleViewAllArticles}
              className="bg-gradient-to-r from-blue-600 to-red-600 text-white px-8 py-3 rounded-lg font-semibold hover:from-blue-700 hover:to-red-700 transition-all duration-300 transform hover:scale-105" 
              data-testid="button-view-all-articles"
            >
              View All Articles ({filteredArticles.length - 3} more)
            </button>
          </div>
        )}

        {showAll && (
          <div className="text-center mt-12">
            <button 
              onClick={() => setShowAll(false)}
              className="bg-gradient-to-r from-gray-600 to-gray-700 text-white px-8 py-3 rounded-lg font-semibold hover:from-gray-700 hover:to-gray-800 transition-all duration-300" 
              data-testid="button-show-less-articles"
            >
              Show Less
            </button>
          </div>
        )}
      </div>

      <BlogDetailModal
        isOpen={showModal}
        onClose={handleCloseModal}
        article={selectedArticle}
      />
    </section>
  );
}

client/src/components/admin/payments-man.content:
import { useState, useEffect } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { 
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { 
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import { 
  CreditCard, 
  DollarSign, 
  Calendar, 
  User, 
  Package,
  Eye,
  CheckCircle,
  XCircle,
  Clock,
  RefreshCw
} from "lucide-react";
import { useToast } from "@/hooks/use-toast";

interface Payment {
  id: string;
  packageId?: string;
  customerName: string;
  customerEmail: string;
  customerPhone: string;
  amount: string;
  currency: string;
  status: 'pending' | 'success' | 'failed' | 'refunded';
  razorpayOrderId?: string;
  razorpayPaymentId?: string;
  razorpaySignature?: string;
  paymentMethod?: string;
  notes?: string;
  createdAt: string;
  paidAt?: string;
}

export default function PaymentsManagement() {
  const [payments, setPayments] = useState<Payment[]>([]);
  const [loading, setLoading] = useState(true);
  const [statusFilter, setStatusFilter] = useState<string>("all");
  const [selectedPayment, setSelectedPayment] = useState<Payment | null>(null);
  const { toast } = useToast();

  useEffect(() => {
    fetchPayments();
  }, []);

  const fetchPayments = async () => {
    try {
      const response = await fetch("/api/admin/payments");
      const data = await response.json();
      if (data.success) {
        setPayments(data.data || []);
      }
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to fetch payments",
        variant: "destructive"
      });
    } finally {
      setLoading(false);
    }
  };

  const getStatusBadge = (status: string) => {
    switch (status) {
      case 'pending':
        return <Badge variant="secondary"><Clock className="h-3 w-3 mr-1" />Pending</Badge>;
      case 'success':
        return <Badge variant="default" className="bg-green-600"><CheckCircle className="h-3 w-3 mr-1" />Success</Badge>;
      case 'failed':
        return <Badge variant="destructive"><XCircle className="h-3 w-3 mr-1" />Failed</Badge>;
      case 'refunded':
        return <Badge variant="outline"><RefreshCw className="h-3 w-3 mr-1" />Refunded</Badge>;
      default:
        return <Badge variant="outline">{status}</Badge>;
    }
  };

  const formatCurrency = (amount: string, currency: string = 'INR') => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: currency
    }).format(parseFloat(amount));
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleString();
  };

  const filteredPayments = payments.filter(payment => 
    statusFilter === "all" || payment.status === statusFilter
  );

  const getTotalStats = () => {
    const total = payments.reduce((sum, p) => sum + parseFloat(p.amount), 0);
    const successful = payments.filter(p => p.status === 'success');
    const successfulAmount = successful.reduce((sum, p) => sum + parseFloat(p.amount), 0);
    const pending = payments.filter(p => p.status === 'pending').length;
    const failed = payments.filter(p => p.status === 'failed').length;

    return {
      total: formatCurrency(total.toString()),
      successful: formatCurrency(successfulAmount.toString()),
      successfulCount: successful.length,
      pending,
      failed,
      successRate: payments.length > 0 ? Math.round((successful.length / payments.length) * 100) : 0
    };
  };

  const stats = getTotalStats();

  if (loading) {
    return (
      <div className="text-center py-8">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600 mx-auto mb-4"></div>
        <p className="text-muted-foreground">Loading payments...</p>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <div>
          <h2 className="text-2xl font-bold">Payments Management</h2>
          <p className="text-muted-foreground">Track customer payments and transactions</p>
        </div>
        <Select value={statusFilter} onValueChange={setStatusFilter}>
          <SelectTrigger className="w-48">
            <SelectValue placeholder="Filter by status" />
          </SelectTrigger>
          <SelectContent>
            <SelectItem value="all">All Payments</SelectItem>
            <SelectItem value="pending">Pending</SelectItem>
            <SelectItem value="success">Success</SelectItem>
            <SelectItem value="failed">Failed</SelectItem>
            <SelectItem value="refunded">Refunded</SelectItem>
          </SelectContent>
        </Select>
      </div>

      {/* Payment Stats */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
        <Card>
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-muted-foreground">Total Revenue</p>
                <p className="text-2xl font-bold text-green-600">{stats.successful}</p>
              </div>
            </div>
          </CardContent>
        </Card>
        <Card>
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-muted-foreground">Successful Payments</p>
                <p className="text-2xl font-bold">{stats.successfulCount}</p>
              </div>
              <CheckCircle className="h-8 w-8 text-green-600" />
            </div>
          </CardContent>
        </Card>
        <Card>
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-muted-foreground">Pending</p>
                <p className="text-2xl font-bold text-yellow-600">{stats.pending}</p>
              </div>
              <Clock className="h-8 w-8 text-yellow-600" />
            </div>
          </CardContent>
        </Card>
        <Card>
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-muted-foreground">Success Rate</p>
                <p className="text-2xl font-bold">{stats.successRate}%</p>
              </div>
              <CreditCard className="h-8 w-8 text-blue-600" />
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Payments List */}
      <div className="grid gap-4">
        {filteredPayments.length === 0 ? (
          <Card>
            <CardContent className="py-8 text-center">
              <CreditCard className="h-12 w-12 text-muted-foreground mx-auto mb-4" />
              <p className="text-muted-foreground">No payments found</p>
            </CardContent>
