import { useState, useEffect } from "react";
import { 
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Badge } from "@/components/ui/badge";
import { Clock, Calendar, User, X } from "lucide-react";
import type { BlogArticle } from "@shared/schema";

interface BlogDetailModalProps {
  isOpen: boolean;
  onClose: () => void;
  article: BlogArticle | null;
}

export default function BlogDetailModal({ isOpen, onClose, article }: BlogDetailModalProps) {
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    if (isOpen && article) {
      setLoading(false);
    }
  }, [isOpen, article]);

  if (!article) return null;

  const getCategoryColor = (category: string) => {
    switch (category) {
      case "students":
        return "bg-blue-100 text-blue-800 border-blue-200";
      case "graduates":
        return "bg-green-100 text-green-800 border-green-200";
      case "professionals":
        return "bg-purple-100 text-purple-800 border-purple-200";
      default:
        return "bg-gray-100 text-gray-800 border-gray-200";
    }
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  const getEmbedUrl = (url: string) => {
    if (!url) return null;

    // YouTube URL patterns
    const youtubeRegex = /(?:youtube\.com\/(?:[^\/]+\/.+\/|(?:v|e(?:mbed)?)\/|.*[?&]v=)|youtu\.be\/)([^"&?\/\s]{11})/;
    const youtubeMatch = url.match(youtubeRegex);
    if (youtubeMatch && youtubeMatch[1]) {
      return `https://www.youtube.com/embed/${youtubeMatch[1]}`;
    }

    // Vimeo URL pattern
    const vimeoRegex = /vimeo\.com\/(?:.*\/)?(\d+)/;
    const vimeoMatch = url.match(vimeoRegex);
    if (vimeoMatch && vimeoMatch[1]) {
      return `https://player.vimeo.com/video/${vimeoMatch[1]}`;
    }

    return null;
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto p-0">
        {loading ? (
          <div className="flex items-center justify-center py-20">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
          </div>
        ) : (
          <>
            {/* Header Image */}
            {article.imageUrl ? (
              <div className="h-64 bg-gradient-to-br from-gray-100 to-gray-200 relative overflow-hidden">
                <img 
                  src={article.imageUrl} 
                  alt={article.title}
                  className="w-full h-full object-cover"
                  onError={(e) => {
                    const target = e.target as HTMLImageElement;
                    target.style.display = 'none';
                    target.nextElementSibling?.classList.remove('hidden');
                  }}
                />
                <div className="hidden h-64 bg-gradient-to-br from-gray-100 to-gray-200 flex items-center justify-center">
                  <div className="text-gray-400 text-8xl">ðŸ“š</div>
                </div>
                <button
                  onClick={onClose}
                  className="absolute top-4 right-4 p-2 bg-black/20 hover:bg-black/40 rounded-full text-white transition-colors"
                  data-testid="button-close-blog-modal"
                >
                  <X className="h-5 w-5" />
                </button>
              </div>
            ) : (
              <div className="h-64 bg-gradient-to-br from-gray-100 to-gray-200 flex items-center justify-center relative overflow-hidden">
                <div className="text-gray-400 text-8xl">ðŸ“š</div>
                <button
                  onClick={onClose}
                  className="absolute top-4 right-4 p-2 bg-black/20 hover:bg-black/40 rounded-full text-white transition-colors"
                  data-testid="button-close-blog-modal"
                >
                  <X className="h-5 w-5" />
                </button>
              </div>
            )}

            <div className="p-8">
              <DialogHeader className="mb-6">
                {/* Category Badge */}
                <div className="flex items-center gap-3 mb-4">
                  <Badge className={`px-3 py-1 border ${getCategoryColor(article.category)}`}>
                    {article.category.charAt(0).toUpperCase() + article.category.slice(1)}
                  </Badge>
                </div>

                {/* Title */}
                <DialogTitle className="text-3xl font-bold text-foreground leading-tight mb-4">
                  {article.title}
                </DialogTitle>

                {/* Meta Information */}
                <div className="flex flex-wrap items-center gap-6 text-sm text-muted-foreground border-b border-border pb-4">
                  <div className="flex items-center gap-2">
                    <Calendar className="h-4 w-4" />
                    {formatDate(article.createdAt.toString())}
                  </div>
                  <div className="flex items-center gap-2">
                    <Clock className="h-4 w-4" />
                    {article.readTime}
                  </div>
                  <div className="flex items-center gap-2">
                    <User className="h-4 w-4" />
                    Careerskope Team
                  </div>
                </div>
              </DialogHeader>

              {/* Description */}
              <div className="prose prose-lg max-w-none">
                <p className="text-xl text-muted-foreground font-medium mb-8 leading-relaxed">
                  {article.description}
                </p>

                {/* Video Embed */}
                {article.videoUrl && getEmbedUrl(article.videoUrl) && (
                  <div className="mb-8">
                    <div className="relative w-full" style={{ paddingBottom: '56.25%' }}>
                      <iframe
                        className="absolute top-0 left-0 w-full h-full rounded-lg"
                        src={getEmbedUrl(article.videoUrl) || ''}
                        title={article.title}
                        frameBorder="0"
                        allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                        allowFullScreen
                      />
                    </div>
                  </div>
                )}

                {/* Content */}
                <div className="text-foreground leading-relaxed space-y-6">
                  {article.content && article.content.trim() ? (
                    <div dangerouslySetInnerHTML={{ __html: article.content }} />
                  ) : (
                    <div className="space-y-6">
                      <p>
                        This comprehensive guide will help you navigate your career journey and make informed decisions about your professional future.
                      </p>

                      <h3 className="text-2xl font-semibold mb-4">Key Insights</h3>

                      <ul className="space-y-3 list-disc list-inside">
                        <li>Understanding industry trends and market demands</li>
                        <li>Developing essential skills for career advancement</li>
                        <li>Building a strong professional network</li>
                        <li>Creating effective career development strategies</li>
                        <li>Balancing personal goals with professional growth</li>
                      </ul>

                      <h3 className="text-2xl font-semibold mb-4">Action Steps</h3>

                      <p>
                        Take the time to reflect on your career goals and create a concrete plan for achieving them. Remember that career development is an ongoing process that requires continuous learning and adaptation.
                      </p>

                      <blockquote className="border-l-4 border-blue-500 pl-6 italic text-lg bg-blue-50 p-4 rounded-r-lg">
                        "Success in your career comes from understanding your strengths, staying adaptable to change, and continuously investing in your professional development."
                      </blockquote>

                      <h3 className="text-2xl font-semibold mb-4">Next Steps</h3>

                      <p>
                        Ready to take the next step in your career journey? Our expert team at Careerskope is here to provide personalized guidance and support. Contact us today to learn about our comprehensive career coaching packages.
                      </p>
                    </div>
                  )}
                </div>

                {/* Call to Action */}
                <div className="mt-12 p-6 bg-gradient-to-r from-blue-50 to-green-50 rounded-xl border border-blue-200">
                  <h3 className="text-xl font-semibold mb-3 text-blue-900">Ready to Advance Your Career?</h3>
                  <p className="text-blue-800 mb-4">
                    Get personalized career guidance from our experts. Explore our solutions and take the first step towards your dream career.
                  </p>
                  <button
                    onClick={() => {
                      onClose();
                      // Scroll to services section
                      document.getElementById('services')?.scrollIntoView({ behavior: 'smooth' });
                    }}
                    className="bg-gradient-to-r from-blue-600 to-green-600 text-white px-6 py-3 rounded-lg font-semibold hover:from-blue-700 hover:to-green-700 transition-all duration-300"
                    data-testid="button-explore-packages"
                  >
                    Explore Our Solutions
                  </button>
                </div>
              </div>
            </div>
          </>
        )}
      </DialogContent>
    </Dialog>
  );
}

client/src/components/admin/testimonials.content:
import { useState, useEffect, useRef } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { Switch } from "@/components/ui/switch";
import { Badge } from "@/components/ui/badge";
import { 
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import { 
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Plus, Edit, Trash2, Users, Star, Upload, Image, X } from "lucide-react";
import { useToast } from "@/hooks/use-toast";

interface Testimonial {
  id: string;
  name: string;
  role: string;
  quote: string;
  initial: string;
  gradient: string;
  imageUrl?: string;
  isActive: boolean;
  featured?: string;
  createdAt: string;
}

const gradientOptions = [
  { value: "bg-gradient-to-br from-blue-500 to-purple-600", label: "Blue to Purple" },
  { value: "bg-gradient-to-br from-green-500 to-blue-600", label: "Green to Blue" },
  { value: "bg-gradient-to-br from-red-500 to-pink-600", label: "Red to Pink" },
  { value: "bg-gradient-to-br from-yellow-500 to-orange-600", label: "Yellow to Orange" },
  { value: "bg-gradient-to-br from-purple-500 to-red-600", label: "Purple to Red" },
  { value: "bg-gradient-to-br from-indigo-500 to-purple-600", label: "Indigo to Purple" },
];

export default function TestimonialsManagement() {
  const [testimonials, setTestimonials] = useState<Testimonial[]>([]);
  const [loading, setLoading] = useState(true);
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [editingTestimonial, setEditingTestimonial] = useState<Testimonial | null>(null);
  const { toast } = useToast();

  useEffect(() => {
    fetchTestimonials();
  }, []);

  const fetchTestimonials = async () => {
    try {
      const response = await fetch("/api/admin/testimonials");
      const data = await response.json();
      if (data.success) {
        setTestimonials(data.data || []);
      }
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to fetch testimonials",
        variant: "destructive"
      });
    } finally {
      setLoading(false);
    }
  };

  const createTestimonial = async (testimonialData: Partial<Testimonial>) => {
    try {
      const response = await fetch("/api/admin/testimonials", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(testimonialData),
      });

      const data = await response.json();
      if (data.success) {
        toast({
          title: "Success",
          description: "Testimonial created successfully",
        });
        fetchTestimonials();
        setShowCreateModal(false);
      } else {
        throw new Error(data.message);
      }
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to create testimonial",
        variant: "destructive"
      });
    }
  };

  const updateTestimonial = async (id: string, testimonialData: Partial<Testimonial>) => {
    try {
      const response = await fetch(`/api/admin/testimonials/${id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(testimonialData),
      });

      const data = await response.json();
      if (data.success) {
        toast({
          title: "Success",
          description: "Testimonial updated successfully",
        });
        fetchTestimonials();
        setEditingTestimonial(null);
      } else {
        throw new Error(data.message);
      }
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to update testimonial",
        variant: "destructive"
      });
    }
  };

  const deleteTestimonial = async (id: string) => {
    if (!confirm("Are you sure you want to delete this testimonial?")) return;

    try {
      const response = await fetch(`/api/admin/testimonials/${id}`, {
        method: "DELETE",
      });

      const data = await response.json();
      if (data.success) {
        toast({
          title: "Success",
          description: "Testimonial deleted successfully",
        });
        fetchTestimonials();
      } else {
        throw new Error(data.message);
      }
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to delete testimonial",
        variant: "destructive"
      });
    }
  };

  if (loading) {
    return (
      <div className="text-center py-8">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600 mx-auto mb-4"></div>
        <p className="text-muted-foreground">Loading testimonials...</p>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <div>
          <h2 className="text-2xl font-bold">Testimonials Management</h2>
          <p className="text-muted-foreground">Manage customer testimonials and reviews</p>
        </div>
        <Dialog open={showCreateModal} onOpenChange={setShowCreateModal}>
          <DialogTrigger asChild>
            <Button data-testid="button-create-testimonial">
              <Plus className="h-4 w-4 mr-2" />
              Add Testimonial
            </Button>
          </DialogTrigger>
          <DialogContent className="max-w-2xl">
            <DialogHeader>
              <DialogTitle>Create New Testimonial</DialogTitle>
            </DialogHeader>
            <TestimonialForm onSubmit={createTestimonial} />
          </DialogContent>
        </Dialog>
      </div>

      <div className="grid gap-6">
        {testimonials.length === 0 ? (
          <Card>
            <CardContent className="py-8 text-center">
              <Users className="h-12 w-12 text-muted-foreground mx-auto mb-4" />
              <p className="text-muted-foreground">No testimonials found</p>
            </CardContent>
          </Card>
        ) : (
          testimonials.map((testimonial) => (
            <Card key={testimonial.id} className="hover:shadow-md transition-shadow">
              <CardContent className="p-6">
                <div className="flex justify-between items-start">
                  <div className="flex-1">
                    <div className="flex items-center gap-4 mb-4">
                      <div className={`w-12 h-12 rounded-full ${testimonial.gradient} flex items-center justify-center text-white font-bold text-lg`}>
                        {testimonial.initial}
                      </div>
                      <div className="flex-1">
                        <div className="flex items-center gap-2 mb-1">
                          <h3 className="font-semibold text-lg">{testimonial.name}</h3>
                          {!testimonial.isActive && <Badge variant="secondary">Inactive</Badge>}
                          {testimonial.featured && <Badge variant="default"><Star className="h-3 w-3 mr-1" />Featured</Badge>}
                        </div>
                        <p className="text-sm text-muted-foreground">{testimonial.role}</p>
                      </div>
                    </div>
                    <blockquote className="text-gray-700 dark:text-gray-300 italic mb-4 pl-4 border-l-4 border-gray-200 dark:border-gray-700">
                      "{testimonial.quote}"
                    </blockquote>
                    <p className="text-xs text-muted-foreground">
                      Created: {new Date(testimonial.createdAt).toLocaleDateString()}
                    </p>
                  </div>
                  <div className="flex gap-2 ml-4">
                    <Dialog>
                      <DialogTrigger asChild>
                        <Button 
                          variant="outline" 
                          size="sm"
                          onClick={() => setEditingTestimonial(testimonial)}
                          data-testid={`button-edit-${testimonial.id}`}
                        >
                          <Edit className="h-4 w-4" />
                        </Button>
                      </DialogTrigger>
                      <DialogContent className="max-w-2xl">
                        <DialogHeader>
                          <DialogTitle>Edit Testimonial</DialogTitle>
                        </DialogHeader>
                        {editingTestimonial && (
                          <TestimonialForm 
                            testimonial={editingTestimonial}
                            onSubmit={(data) => updateTestimonial(editingTestimonial.id, data)}
                          />
                        )}
                      </DialogContent>
                    </Dialog>
                    <Button 
                      variant="outline" 
                      size="sm" 
                      onClick={() => deleteTestimonial(testimonial.id)}
                      data-testid={`button-delete-${testimonial.id}`}
                    >
                      <Trash2 className="h-4 w-4 text-red-500" />
                    </Button>
                  </div>
                </div>
              </CardContent>
            </Card>
          ))
        )}
      </div>
    </div>
  );
}

function TestimonialForm({ 
  testimonial, 
  onSubmit 
}: { 
  testimonial?: Testimonial; 
  onSubmit: (data: Partial<Testimonial>) => void; 
}) {
  const { toast } = useToast();
  const fileInputRef = useRef<HTMLInputElement>(null);
  const [uploading, setUploading] = useState(false);
  const [formData, setFormData] = useState({
    name: testimonial?.name || "",
    role: testimonial?.role || "",
    quote: testimonial?.quote || "",
    initial: testimonial?.initial || "",
    gradient: testimonial?.gradient || gradientOptions[0].value,
    imageUrl: testimonial?.imageUrl || "",
    isActive: testimonial?.isActive ?? true,
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    onSubmit(formData);
  };

  const handleNameChange = (name: string) => {
    setFormData(prev => ({
      ...prev,
      name,
      initial: name.charAt(0).toUpperCase()
    }));
  };

  const handleFileUpload = async (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (!file) return;

    if (file.size > 5 * 1024 * 1024) {
      toast({
        title: "File too large",
        description: "Please select an image under 5MB",
        variant: "destructive"
      });
      return;
    }

    setUploading(true);
    try {
      const formDataUpload = new FormData();
      formDataUpload.append('image', file);
      formDataUpload.append('type', 'testimonial');

      const response = await fetch('/api/admin/upload', {
        method: 'POST',
        body: formDataUpload,
      });

      const data = await response.json();
      if (data.success) {
        setFormData(prev => ({ ...prev, imageUrl: data.url }));
        toast({
          title: "Image uploaded",
          description: "Profile photo uploaded successfully",
        });
      } else {
        throw new Error(data.message);
      }
    } catch (error) {
      toast({
        title: "Upload failed",
        description: "Failed to upload image. Please try again.",
        variant: "destructive"
      });
    } finally {
      setUploading(false);
      if (fileInputRef.current) {
        fileInputRef.current.value = '';
      }
    }
  };

  const clearImage = () => {
    setFormData(prev => ({ ...prev, imageUrl: "" }));
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-6">
      <div className="grid grid-cols-2 gap-4">
        <div>
          <Label htmlFor="name">Name</Label>
          <Input
            id="name"
            value={formData.name}
            onChange={(e) => handleNameChange(e.target.value)}
            required
            data-testid="input-name"
          />
        </div>
        <div>
          <Label htmlFor="role">Role/Position</Label>
          <Input
            id="role"
            value={formData.role}
            onChange={(e) => setFormData(prev => ({ ...prev, role: e.target.value }))}
            required
            data-testid="input-role"
          />
        </div>
      </div>

      <div>
        <Label htmlFor="quote">Testimonial Quote</Label>
        <Textarea
          id="quote"
          value={formData.quote}
          onChange={(e) => setFormData(prev => ({ ...prev, quote: e.target.value }))}
          required
          rows={4}
          data-testid="textarea-quote"
        />
      </div>

      <div>
        <Label>Profile Photo</Label>
        <div className="mt-2 space-y-3">
          {formData.imageUrl ? (
            <div className="flex items-center gap-4">
              <div className="relative">
                <img 
                  src={formData.imageUrl} 
                  alt="Profile preview" 
                  className="w-16 h-16 rounded-full object-cover border-2 border-gray-200"
                />
                <button
                  type="button"
                  onClick={clearImage}
                  className="absolute -top-1 -right-1 bg-red-500 text-white rounded-full p-0.5 hover:bg-red-600"
                >
                  <X className="h-3 w-3" />
                </button>
              </div>
              <span className="text-sm text-muted-foreground truncate max-w-[200px]">
                {formData.imageUrl}
              </span>
            </div>
          ) : (
            <div className="flex items-center gap-2 text-sm text-muted-foreground">
              <Image className="h-4 w-4" />
              <span>No image uploaded</span>
            </div>
          )}

          <div className="flex items-center gap-3">
            <input
              ref={fileInputRef}
              type="file"
              accept="image/jpeg,image/png,image/webp"
              onChange={handleFileUpload}
              className="hidden"
              data-testid="input-file-upload"
            />
            <Button
              type="button"
              variant="outline"
              size="sm"
              onClick={() => fileInputRef.current?.click()}
              disabled={uploading}
              data-testid="button-upload-image"
            >
              {uploading ? (
                <>
                  <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-current mr-2"></div>
                  Uploading...
                </>
              ) : (
                <>
                  <Upload className="h-4 w-4 mr-2" />
                  Upload Photo
                </>
              )}
            </Button>
            <span className="text-xs text-muted-foreground">
              JPG, PNG, WebP (max 5MB)
            </span>
          </div>

          <div className="flex items-center gap-2">
            <span className="text-xs text-muted-foreground">Or enter URL:</span>
            <Input
              value={formData.imageUrl}
              onChange={(e) => setFormData(prev => ({ ...prev, imageUrl: e.target.value }))}
              placeholder="https://..."
              className="flex-1 text-sm"
              data-testid="input-image-url"
            />
          </div>
        </div>
      </div>

      <div className="grid grid-cols-2 gap-4">
        <div>
          <Label htmlFor="initial">Initial</Label>
          <Input
            id="initial"
            value={formData.initial}
            onChange={(e) => setFormData(prev => ({ ...prev, initial: e.target.value.charAt(0).toUpperCase() }))}
            maxLength={1}
            required
            data-testid="input-initial"
          />
        </div>
        <div>
          <Label htmlFor="gradient">Avatar Gradient (fallback)</Label>
          <Select 
            value={formData.gradient} 
            onValueChange={(value) => setFormData(prev => ({ ...prev, gradient: value }))}
          >
            <SelectTrigger data-testid="select-gradient">
              <SelectValue />
            </SelectTrigger>
            <SelectContent>
              {gradientOptions.map((option) => (
                <SelectItem key={option.value} value={option.value}>
                  <div className="flex items-center gap-2">
                    <div className={`w-4 h-4 rounded ${option.value}`}></div>
                    {option.label}
                  </div>
                </SelectItem>
              ))}
            </SelectContent>
          </Select>
        </div>
      </div>

      <div className="flex items-center space-x-2">
        <Switch
          id="is-active"
          checked={formData.isActive}
          onCheckedChange={(checked) => setFormData(prev => ({ ...prev, isActive: checked }))}
          data-testid="switch-active"
        />
        <Label htmlFor="is-active">Active (visible on website)</Label>
      </div>

      <div className="flex justify-end gap-2">
        <Button type="submit" disabled={uploading} data-testid="button-submit">
          {testimonial ? "Update" : "Create"} Testimonial
        </Button>
      </div>
    </form>
  );
}

