server/storage.ts.content:
import { 
  users, 
  contactSubmissions, 
  blogArticles,
  testimonials,
  packages,
  customizePlans,
  payments,
  adminUsers,
  photoGallery,
  workshopBookings,
  type User, 
  type InsertUser, 
  type ContactSubmission, 
  type InsertContact,
  type BlogArticle,
  type InsertBlogArticle,
  type Testimonial,
  type InsertTestimonial,
  type Package,
  type InsertPackage,
  type CustomizePlan,
  type InsertCustomizePlan,
  type Payment,
  type InsertPayment,
  type AdminUser,
  type InsertAdminUser,
  type PhotoGallery,
  type InsertPhotoGallery,
  type WorkshopBooking,
  type InsertWorkshopBooking,
  type UpdateContactStatus
} from "@shared/schema";
import { db } from "./db";
import { eq, desc, sql } from "drizzle-orm";

export interface IStorage {
  // User operations
  getUser(id: string): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;

  // Admin user operations
  getAdminUser(id: string): Promise<AdminUser | undefined>;
  getAdminUserByUsername(username: string): Promise<AdminUser | undefined>;
  getAdminUserByEmail(email: string): Promise<AdminUser | undefined>;
  createAdminUser(adminUser: InsertAdminUser): Promise<AdminUser>;
  updateAdminLastLogin(id: string): Promise<void>;

  // Contact submissions
  createContactSubmission(contact: InsertContact): Promise<ContactSubmission>;
  getContactSubmissions(): Promise<ContactSubmission[]>;
  updateContactStatus(id: string, status: UpdateContactStatus): Promise<ContactSubmission>;

  // Blog articles
  getBlogArticles(): Promise<BlogArticle[]>;
  getBlogArticlesByCategory(category: string): Promise<BlogArticle[]>;
  createBlogArticle(article: InsertBlogArticle): Promise<BlogArticle>;
  updateBlogArticle(id: string, article: Partial<InsertBlogArticle>): Promise<BlogArticle>;
  deleteBlogArticle(id: string): Promise<void>;

  // Testimonials
  getTestimonials(): Promise<Testimonial[]>;
  getActiveTestimonials(): Promise<Testimonial[]>;
  createTestimonial(testimonial: InsertTestimonial): Promise<Testimonial>;
  updateTestimonial(id: string, testimonial: Partial<InsertTestimonial>): Promise<Testimonial>;
  deleteTestimonial(id: string): Promise<void>;

  // Packages
  getPackages(): Promise<Package[]>;
  getActivePackages(): Promise<Package[]>;
  getPackagesByCategory(category: string): Promise<Package[]>;
  getPackage(id: string): Promise<Package | undefined>;
  createPackage(pkg: InsertPackage): Promise<Package>;
  updatePackage(id: string, pkg: Partial<InsertPackage>): Promise<Package>;
  archivePackage(id: string): Promise<Package>;
  deletePackage(id: string): Promise<void>;
  hasPaymentsForPackage(id: string): Promise<boolean>;

  // Customize Plans
  getCustomizePlans(): Promise<CustomizePlan[]>;
  getActiveCustomizePlans(): Promise<CustomizePlan[]>;
  getCustomizePlan(id: string): Promise<CustomizePlan | undefined>;
  createCustomizePlan(plan: InsertCustomizePlan): Promise<CustomizePlan>;
  updateCustomizePlan(id: string, plan: Partial<InsertCustomizePlan>): Promise<CustomizePlan>;
  deleteCustomizePlan(id: string): Promise<void>;

  // Payments
  getPayments(): Promise<Payment[]>;
  getPayment(id: string): Promise<Payment | undefined>;
  getPaymentByOrderId(orderId: string): Promise<Payment | undefined>;
  createPayment(payment: InsertPayment): Promise<Payment>;
  updatePayment(id: string, payment: Partial<InsertPayment>): Promise<Payment>;
  updatePaymentStatus(id: string, status: string, paymentDetails?: any): Promise<Payment>;

  // Photo Gallery
  getPhotoGalleryItems(): Promise<PhotoGallery[]>;
  getActivePhotoGalleryItems(): Promise<PhotoGallery[]>;
  createPhotoGalleryItem(photo: InsertPhotoGallery): Promise<PhotoGallery>;
  deletePhotoGalleryItem(id: string): Promise<void>;

  // Workshop Bookings
  getWorkshopBookings(): Promise<WorkshopBooking[]>;
  getWorkshopBooking(id: string): Promise<WorkshopBooking | undefined>;
  createWorkshopBooking(booking: InsertWorkshopBooking): Promise<WorkshopBooking>;
  updateWorkshopBookingStatus(id: string, status: string, adminNotes?: string): Promise<WorkshopBooking>;
  deleteWorkshopBooking(id: string): Promise<void>;
}

export class DatabaseStorage implements IStorage {
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user || undefined;
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.username, username));
    return user || undefined;
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(insertUser)
      .returning();
    return user;
  }

  async createContactSubmission(insertContact: InsertContact): Promise<ContactSubmission> {
    const [contact] = await db
      .insert(contactSubmissions)
      .values(insertContact)
      .returning();
    return contact;
  }

