      .update(adminUsers)
      .set({ lastLogin: new Date() })
      .where(eq(adminUsers.id, id));
  }

  // Contact submissions updates
  async updateContactStatus(id: string, status: UpdateContactStatus): Promise<ContactSubmission> {
    const updateData: any = {
      status: status.status,
      respondedAt: status.status === 'responded' ? new Date() : null,
    };

    if (status.adminNotes !== undefined) {
      updateData.adminNotes = status.adminNotes;
    }

    if (status.respondedBy !== undefined) {
      updateData.respondedBy = status.respondedBy;
    }

    const [updated] = await db
      .update(contactSubmissions)
      .set(updateData)
      .where(eq(contactSubmissions.id, id))
      .returning();
    return updated;
  }

  // Testimonials updates
  async getActiveTestimonials(): Promise<Testimonial[]> {
    return await db.select().from(testimonials)
      .where(eq(testimonials.isActive, true))
      .orderBy(desc(sql`COALESCE(${testimonials.featured}, ${testimonials.createdAt})`));
  }

  // Packages operations
  async getPackages(): Promise<Package[]> {
    return await db.select().from(packages).orderBy(desc(packages.createdAt));
  }

  async getActivePackages(): Promise<Package[]> {
    return await db.select().from(packages)
      .where(eq(packages.isActive, true))
      .orderBy(desc(packages.createdAt));
  }

  async getPackagesByCategory(category: string): Promise<Package[]> {
    return await db.select().from(packages)
      .where(sql`${packages.category} = ${category} AND ${packages.isActive} = true`)
      .orderBy(desc(packages.createdAt));
  }

  async getPackage(id: string): Promise<Package | undefined> {
    const [pkg] = await db.select().from(packages).where(eq(packages.id, id));
    return pkg || undefined;
  }

  async createPackage(pkg: InsertPackage): Promise<Package> {
    const [newPackage] = await db
      .insert(packages)
      .values(pkg)
      .returning();
    return newPackage;
  }

  async updatePackage(id: string, pkg: Partial<InsertPackage>): Promise<Package> {
    const [updatedPackage] = await db
      .update(packages)
      .set(pkg)
      .where(eq(packages.id, id))
      .returning();
    return updatedPackage;
  }

  async archivePackage(id: string): Promise<Package> {
    const [archivedPackage] = await db
      .update(packages)
      .set({ isActive: false })
      .where(eq(packages.id, id))
      .returning();
    return archivedPackage;
  }

  async deletePackage(id: string): Promise<void> {
    const hasPayments = await this.hasPaymentsForPackage(id);
    if (hasPayments) {
      throw new Error("PACKAGE_HAS_PAYMENTS");
    }
    await db.delete(packages).where(eq(packages.id, id));
  }

  async hasPaymentsForPackage(id: string): Promise<boolean> {
    const result = await db
      .select({ count: sql<number>`count(*)` })
      .from(payments)
      .where(eq(payments.packageId, id));
    return result[0]?.count > 0;
  }

  // Customize Plans operations
  async getCustomizePlans(): Promise<CustomizePlan[]> {
    return await db.select().from(customizePlans).orderBy(customizePlans.displayOrder);
  }

  async getActiveCustomizePlans(): Promise<CustomizePlan[]> {
    return await db.select().from(customizePlans)
      .where(eq(customizePlans.isActive, true))
      .orderBy(customizePlans.displayOrder);
  }

  async getCustomizePlan(id: string): Promise<CustomizePlan | undefined> {
    const [plan] = await db.select().from(customizePlans).where(eq(customizePlans.id, id));
    return plan || undefined;
  }

  async createCustomizePlan(plan: InsertCustomizePlan): Promise<CustomizePlan> {
    const [newPlan] = await db
      .insert(customizePlans)
      .values(plan)
      .returning();
    return newPlan;
  }

  async updateCustomizePlan(id: string, plan: Partial<InsertCustomizePlan>): Promise<CustomizePlan> {
    const [updatedPlan] = await db
      .update(customizePlans)
      .set(plan)
      .where(eq(customizePlans.id, id))
      .returning();
    return updatedPlan;
  }

  async deleteCustomizePlan(id: string): Promise<void> {
    await db.delete(customizePlans).where(eq(customizePlans.id, id));
  }

  // Payments operations
  async getPayments(): Promise<Payment[]> {
    return await db.select().from(payments).orderBy(desc(payments.createdAt));
  }

  async getPayment(id: string): Promise<Payment | undefined> {
    const [payment] = await db.select().from(payments).where(eq(payments.id, id));
    return payment || undefined;
  }

  async getPaymentByOrderId(orderId: string): Promise<Payment | undefined> {
    const [payment] = await db.select().from(payments).where(eq(payments.razorpayOrderId, orderId));
    return payment || undefined;
  }

  async createPayment(payment: InsertPayment): Promise<Payment> {
    const [newPayment] = await db
      .insert(payments)
      .values(payment)
      .returning();
    return newPayment;
  }

  async updatePayment(id: string, payment: Partial<InsertPayment>): Promise<Payment> {
    const [updatedPayment] = await db
      .update(payments)
      .set(payment)
      .where(eq(payments.id, id))
      .returning();
    return updatedPayment;
  }

  async updatePaymentStatus(id: string, status: string, paymentDetails?: any): Promise<Payment> {
    const updateData: any = {
      status,
      paidAt: status === 'success' ? new Date() : null,
    };

    if (paymentDetails) {
      if (paymentDetails.paymentId) updateData.razorpayPaymentId = paymentDetails.paymentId;
      if (paymentDetails.signature) updateData.razorpaySignature = paymentDetails.signature;
      if (paymentDetails.method) updateData.paymentMethod = paymentDetails.method;
    }

    const [updatedPayment] = await db
      .update(payments)
      .set(updateData)
      .where(eq(payments.id, id))
      .returning();
    return updatedPayment;
  }

  // Photo Gallery operations
  async getPhotoGalleryItems(): Promise<PhotoGallery[]> {
    return await db.select().from(photoGallery)
      .orderBy(photoGallery.displayOrder, desc(photoGallery.createdAt));
  }

  async getActivePhotoGalleryItems(): Promise<PhotoGallery[]> {
    return await db.select().from(photoGallery)
      .where(eq(photoGallery.isActive, true))
      .orderBy(photoGallery.displayOrder, desc(photoGallery.createdAt));
  }

  async createPhotoGalleryItem(photo: InsertPhotoGallery): Promise<PhotoGallery> {
    const [newPhoto] = await db
      .insert(photoGallery)
      .values(photo)
      .returning();
    return newPhoto;
  }

  async deletePhotoGalleryItem(id: string): Promise<void> {
    await db.delete(photoGallery).where(eq(photoGallery.id, id));
  }

  // Workshop Bookings
  async getWorkshopBookings(): Promise<WorkshopBooking[]> {
    return await db.select().from(workshopBookings).orderBy(desc(workshopBookings.createdAt));
  }

  async getWorkshopBooking(id: string): Promise<WorkshopBooking | undefined> {
    const [booking] = await db.select().from(workshopBookings).where(eq(workshopBookings.id, id));
    return booking || undefined;
  }

  async createWorkshopBooking(booking: InsertWorkshopBooking): Promise<WorkshopBooking> {
    const [newBooking] = await db
      .insert(workshopBookings)
      .values(booking)
      .returning();
    return newBooking;
  }

  async updateWorkshopBookingStatus(id: string, status: string, adminNotes?: string): Promise<WorkshopBooking> {
    const [updatedBooking] = await db
      .update(workshopBookings)
      .set({ status, adminNotes })
      .where(eq(workshopBookings.id, id))
      .returning();
    return updatedBooking;
  }

  async deleteWorkshopBooking(id: string): Promise<void> {
    await db.delete(workshopBookings).where(eq(workshopBookings.id, id));
  }
}

